import { useState, useEffect } from "react";
import "../style/flipText.css";

type Word = {
	data: string[][];
};

function FlipText({ data }: Word) {
	const [HtmlArr, setHtmlArr] = useState(data[0]);

	useEffect(() => {
		for (let i = 0; i < data.length; i++) {
			console.log(data.length);

			let letterArr: string[] = data[i];
			//setTimeout(() => {
			for (let j = 0; j < data[i].length; j++) {
				console.log(data[i]);

				let letter: string = letterArr[i];
				let temp: string[] = HtmlArr;
				temp.push(letter);
				setHtmlArr(temp);
			}
			//}, 2000);
		}
	}, []);

	return (
		<div id='flip_container'>
			{HtmlArr.map((element, index) => {
				return (
					<div className='letter' key={index}>
						{element}
					</div>
				);
			})}
		</div>
	);
}
export default FlipText;
