import { useState, useEffect } from "react";
import Rating from "@mui/material/Rating";
import Box from "@mui/material/Box";
import "../style/sneakersCards.css";

type cardSetting = {
	price: string;
	rating: number;
	img: string;
	promo: string;
	color: string;
};
interface IRating {
	value: number;
	text?: string;
	color?: string;
	size?: string;
}

function SneakersCard({ price, rating, img, promo, color }: cardSetting) {
	const [Promo, setPromo] = useState(promo);
	const [PromoState, setPromoState] = useState("none");
	useEffect(() => {
		if (promo == "") {
			setPromoState("none");
		}
	});

	return (
		<div className='card_main_container'>
			<div className='upper_section_container'>
				<img className='card_img' src={img} />
				<div className='card_info_container'>
					<div className='offer'>{Promo}</div>
					<div style={{ display: " { PromoState }" }} className='preview_price'>
						${price}
					</div>
				</div>
			</div>
			<div className='star_Rating'>
				<Box>
					<Rating
						name='sneakers_rating'
						value={rating}
						readOnly
						precision={0.5}
					/>
				</Box>
			</div>
		</div>
	);
}
export default SneakersCard;
