import { useState, useEffect } from "react";

type carouselImg = {
	data: string[];
};

function Carousel(data: carouselImg) {
	const [CarouselIndex, setCarouselIndex] = useState(0);
	const [CarouselArr, setCarouselArr] = useState(data);
	const [SwitchTracker, setSwitchTracker] = useState({
		car1: "",
		src1: "",
		car2: "",
		src2: "",
	});

	useEffect(() => {
		setInterval(Tick, 1000);
	});

	function Tick() {
		let element = document.getElementById("carousel_1_img")!;
		element.classList.add("active");

		element.classList.remove("active");
		let myTimeout = setTimeout(function () {}, 500);
		clearTimeout(myTimeout);
	}
	return (
		<div id='hero_container'>
			<div id='hero_imgs'>
				<img
					id='carousel_1_img'
					className='carousel_img'
					src='https://cms-static.asics.com/media-libraries/54842/file.jpg?1612484303015?'
				/>
				<img
					id='carousel_2_img'
					className='carousel_img'
					src='https://cdna.artstation.com/p/assets/images/images/047/520/028/large/rahul-chandra-nike-show.jpg?1647799767'
				/>
			</div>
			<div id='hero_imgs_paging'></div>
		</div>
	);
}

export default Carousel;
