import { useState, useEffect } from "react";
import data from "../data/sneakers_data.json";
import "../style/itemInfo.css";

type Sneakers = {
	company: string;
	name: string;
	description: string;
	price: number;
	deal: number;
};

function ItemInfo() {
	const [Info, setInfo] = useState<Sneakers | undefined>();
	let CurrentPrice = "Unavailable";
	let Deal = "";

	useEffect(() => {
		setInfo(data.info);
		console.log(data);

		if (Info != undefined) {
			CurrentPrice = (Info.price * Info.deal).toString();
		}
	}, [Info]);

	function changeDisplay(src: string) {}

	return (
		<div className='info_container'>
			<div className='company'>{Info?.company}</div>
			<div className='name'>{Info?.name}</div>
			<div className='description'>{Info?.description}</div>
			<div className='current_price'>${CurrentPrice}</div>
			<div className='price'>{Info?.price}</div>
		</div>
	);
}

export default ItemInfo;
