import { useState, useEffect } from "react";
import "../style/display.css";

type Sneakers = {
	id: string;
	name: string;
	img: { preview: string }[];
	info: {
		company: string;
		name: string;
		description: string;
		price: number;
		deal: number;
	};
};

function Display(imageSrc: Sneakers) {
	const [Images, setImages] = useState("");
	const [ImagesIndex, setImagesIndex] = useState(0);

	const [Data, setData] = useState<Sneakers | undefined>();
	useEffect(() => {
		setData(imageSrc);
		setImages(imageSrc?.img?.[0]?.preview);
	}, [Data]);

	function changeDisplay(src: string, index: number) {
		setImages(src);
		setImagesIndex(index);
	}

	return (
		<div className='main_img_container'>
			<img src={Images} id={ImagesIndex} className='main_img' />
			<div className='preview_img_container'>
				{Data?.img?.map((data, index) => {
					let prvClass = "preview_imags" + index;
					return (
						<img
							onClick={() => changeDisplay(data.preview, index)}
							key={index}
							src={data.preview}
							className={prvClass}
						/>
					);
				})}
			</div>
		</div>
	);
}

export default Display;
