import { useState, useEffect } from "react";
import "../style/flipText.css";

type Word = {
	data: string[][];
};

function FlipText({ data }: Word) {
	const [HtmlArr, setHtmlArr] = useState([""]);

	useEffect(() => {
		for (let i = 0; i < data.length; i++) {
			let letterArr: string[] = data[i];
			console.log(letterArr);
			setHtmlArr([""]);

			//setTimeout(() => {
			for (let j = 0; j < 7; j++) {
				let letter: string = letterArr[j];
				let temp: string[] = HtmlArr;
				temp.push(letter);

				console.log(temp);
				setHtmlArr(temp);
			}
			//}, 2000);
		}
	}, []);

	return (
		<div id='flip_container'>
			{HtmlArr.map((element, index) => {
				return (
					<div className='letter' key={index}>
						{element}
					</div>
				);
			})}
		</div>
	);
}
export default FlipText;
