import { useState, useEffect } from "react";

import "../style/itemInfo.css";

type Sneakers = {
	company: string;
	name: string;
	description: string;
	price: number;
	deal: number;
};
type propData = {
	all: {
		id: string;
		name: string;
		img: { preview: string }[];
		info: {
			company: string;
			name: string;
			description: string;
			price: number;
			deal: number;
		};
	};
};
function ItemInfo({ all }: propData) {
	const [Info, setInfo] = useState<Sneakers | undefined>();
	const [CurrentPrice, setCurrentPrice] = useState("Unavailable");
	const [Quantity, setQuantity] = useState(0);
	const [Deal, setDeal] = useState("");
	const [Price, setPrice] = useState("");

	useEffect(() => {
		setInfo(all.info);
		if (Info !== undefined) {
			setDeal(100 * Info.deal + "%");
			setPrice(Info.deal !== 1 ? "$" + Info.price.toFixed(2) : "");
			setCurrentPrice(
				("$" + (Info.price - Info.price * Info.deal).toFixed(2)).toString()
			);
		}
	}, [Info, CurrentPrice, Deal, Price, all.info]);

	function AddQuantity() {
		setQuantity(Quantity + 1);
	}
	function SubQuantity() {
		Quantity > 0 && setQuantity(Quantity - 1);
	}

	return (
		<div className='info_container'>
			<div className='company'>{Info?.company?.toUpperCase()}</div>
			<div className='name'>{Info?.name}</div>
			<div className='description'>{Info?.description}</div>
			<div className='prices_container'>
				<div className='current_price'>{CurrentPrice}</div>
				{Deal != "0%" && <div className='deal'>{Deal}</div>}
				{Deal != "0%" && <div className='price'>{Price}</div>}
			</div>
			<div className='action_container'>
				<div className='quantity_container'>
					<div onClick={() => SubQuantity()} className='quantity_minus'>
						-
					</div>
					<div className='quantity'>{Quantity}</div>
					<div onClick={() => AddQuantity()} className='quantity_plus'>
						+
					</div>
				</div>
				<div className='btn_add_cart'>
					<svg width='22' height='20' xmlns='http://www.w3.org/2000/svg'>
						<path
							d='M20.925 3.641H3.863L3.61.816A.896.896 0 0 0 2.717 0H.897a.896.896 0 1 0 0 1.792h1l1.031 11.483c.073.828.52 1.726 1.291 2.336C2.83 17.385 4.099 20 6.359 20c1.875 0 3.197-1.87 2.554-3.642h4.905c-.642 1.77.677 3.642 2.555 3.642a2.72 2.72 0 0 0 2.717-2.717 2.72 2.72 0 0 0-2.717-2.717H6.365c-.681 0-1.274-.41-1.53-1.009l14.321-.842a.896.896 0 0 0 .817-.677l1.821-7.283a.897.897 0 0 0-.87-1.114ZM6.358 18.208a.926.926 0 0 1 0-1.85.926.926 0 0 1 0 1.85Zm10.015 0a.926.926 0 0 1 0-1.85.926.926 0 0 1 0 1.85Zm2.021-7.243-13.8.81-.57-6.341h15.753l-1.383 5.53Z'
							fill='rgb(255, 255, 255)'
						/>
					</svg>
					Add to cart
				</div>
			</div>
		</div>
	);
}

export default ItemInfo;
