import { useState, useEffect } from "react";
import $ from "jquery";
import "../style/navigationBar.css";
import { ReactComponent as CartIcon } from "../images/icon-cart.svg";
import { ReactComponent as MenuIcon } from "../images/icon-menu.svg";
import profile from "../images/image-avatar.png";

interface pageLocation {
	data: string;
}

function NavigationBar({ data }: pageLocation) {
	const [MenuState, setMenuState] = useState(false);
	// $(document).ready(function () {
	// 	$(".nav_burger_menu_container").click(function () {
	// 		if (MenuState) {
	// 			$(this).children().removeClass("active");
	// 			setMenuState(!MenuState);
	// 		} else {
	// 			$(this).children().addClass("active");
	// 			setMenuState(!MenuState);
	// 		}
	// 	});
	// 	$("li a").click(function () {
	// 		$(this).parent().siblings().removeClass("active");

	// 		$(this).parent().addClass("active");
	// 	});
	// });
	function changeUnderline(Classs: string) {
		let elementAll = document.getElementByTagName("li");
		elementAll.classList.remove("active");

		let element = document.getElementByClassName(Classs);
		element.classList.add("active");
	}

	return (
		<div className='nav_container'>
			<div className='nav_flex_element'>
				<div className='nav_burger_menu_container'>
					{/* <MenuIcon /> */}
					<div className='nav_burger_menu1'></div>
					<div className='nav_burger_menu2'></div>
					<div className='nav_burger_menu3'></div>
				</div>
				<div className='nav_logo_container'></div>
				<ul className='nav_pages'>
					<li className='Collection'>
						<a>Collection</a>
					</li>
					<li onClick={() => changeUnderline("Men")} className='Men'>
						<a>Men</a>
					</li>
					<li onClick={() => changeUnderline("women")} className='Women'>
						<a>Women</a>
					</li>
					<li onClick={() => changeUnderline("About")} className='About'>
						<a>About</a>
					</li>
					<li onClick={() => changeUnderline("Contact")} className='Contact'>
						<a>Contact</a>
					</li>
					<div className='nav_underline'></div>
				</ul>
			</div>
			<div className='nav_flex_element'>
				<div className='nav_cart'>
					<CartIcon />
				</div>
				<div className='nav_profile'>
					<img className='img_profile' alt='profile' src={profile} />
				</div>
			</div>
		</div>
	);
}

export default NavigationBar;
