import { useState, useEffect } from "react";
import "../style/flipText.css";

type Word = {
	data: string[][];
};

function FlipText({ data }: Word) {
	const [HtmlArr, setHtmlArr] = useState(data[1]);

	useEffect(() => {
		for (let i = 0; i < data.length; i++) {
			let letterArr: string[] = data[i];
			setTimeout(() => {
				for (let i = 0; i < letterArr.length; i++) {
					let letter: string = letterArr[i];
					let temp = HtmlArr;
					temp = temp.push(letter);
					setHtmlArr();
				}
			}, 2000);
		}
	}, []);

	return (
		<div id='flip_container'>
			{HtmlArr.map(element, (index) => {
				return (
					<div className='letter' key={index}>
						{element}
					</div>
				);
			})}
		</div>
	);
}
export default FlipText;
