import { useState, useEffect } from "react";
import SneakersCard from "../components/sneakersCard";
import data from "../data/shoesList_men_data.json";
type Data = {
	text: string;
	id: string[];
};
type Sneakers = {
	price: string;
	rating: string;
	imgUrl: string;
	promotion: string;
};

function Promoted({ text, id }: Data) {
	const [Load, setLoad] = useState(true);
	const [DisplayData, setDisplayData] = useState([
		{
			price: "",
			rating: "",
			imgUrl: "",
			promotion: "",
		},
		{
			price: "",
			rating: "",
			imgUrl: "",
			promotion: "",
		},
	]);

	useEffect(() => {
		let temp: Sneakers[] = [];
		data.forEach((element, index) => {
			if (element.id === id[0]) {
				temp.push({
					price: element.price,
					rating: element.rating,
					imgUrl: element.imgUrl,
					promotion: element.promotion,
				});
			} else if (element.id === id[1]) {
				temp.push({
					price: element.price,
					rating: element.rating,
					imgUrl: element.imgUrl,
					promotion: element.promotion,
				});
			}
		});
		setDisplayData(temp);
		setLoad(false);
	});

	return (
		<div id='promoted_container'>
			<div id='type_container'>
				<div id='type_text'>{text}</div>
				<div id='type_decoration'></div>
			</div>
			<div id='sample_container'>
				<div className='sample'>
					{Load ? (
						<div>Loading</div>
					) : (
						<SneakersCard
							className='promoted_img'
							price={DisplayData[0].price}
							rating={parseFloat(DisplayData[0].rating)}
							img={DisplayData[0].imgUrl}
							starSize='medium'
							promo={DisplayData[0].promotion}
						/>
					)}
				</div>
				<div className='sample'>
					{Load ? (
						<div>Loading</div>
					) : (
						<SneakersCard
							className='promoted_img'
							price={DisplayData[1].price}
							rating={parseFloat(DisplayData[1].rating)}
							img={DisplayData[1].imgUrl}
							starSize='medium'
							promo={DisplayData[1].promotion}
						/>
					)}
				</div>
			</div>
		</div>
	);
}
export default Promoted;
