import { useState, useEffect } from "react";

type carouselImg = {
	data: string[];
};

function Carousel(data: carouselImg) {
	const [CarouselIndex, setCarouselIndex] = useState(0);
	const [CarouselArr, setCarouselArr] = useState(data);
	const [SwitchTracker, setSwitchTracker] = useState({
		src1: "",
		src2: "",
	});

	useEffect(() => {
		if (CarouselArr.data.length > 1) {
			setSwitchTracker({
				src1: CarouselArr.data[CarouselIndex],
				src2: CarouselArr.data[CarouselIndex + 1],
			});
			console.log("initial");
			Tick();
		}
	}, [CarouselIndex]);

	function Tick() {
		setTimeout(() => {
			console.log("tiking");

			let element = document.getElementById("carousel_1_img")!;

			element.classList.remove("active");
			element.classList.add("active");

			console.log(CarouselIndex + 1 + "    ====  " + CarouselArr.data.length);

			if (CarouselIndex + 1 > CarouselArr.data.length) {
				setCarouselIndex(0);
				console.log("to 0");

				setSwitchTracker({
					src1: CarouselArr.data[CarouselIndex],
					src2: CarouselArr.data[CarouselIndex + 1],
				});
			} else {
				setSwitchTracker({
					src1: CarouselArr.data[CarouselIndex],
					src2: CarouselArr.data[CarouselIndex + 1],
				});
				let holder = CarouselIndex + 1;
				setCarouselIndex(holder);
				console.log(CarouselIndex + 1);
			}
		}, 5000);
	}
	return (
		<div id='hero_container'>
			<div id='hero_imgs'>
				<img
					id='carousel_1_img'
					className='carousel_img'
					src={SwitchTracker.src1}
				/>
				<img
					id='carousel_2_img'
					className='carousel_img'
					src={SwitchTracker.src2}
				/>
			</div>
			<div id='hero_imgs_paging'></div>
		</div>
	);
}

export default Carousel;
