		// 		src2: CarouselArr.data[CarouselIndex + 1],
		// 	});
		// 	console.log("initial");
		Tick();
		// }
	}, [CarouselIndex]);

	function Tick() {
		let element = document.getElementById("hero_imgs_mapper")!;
		if (CarouselIndex + 1 > CarouselArr.length) {
			console.log("here");
			setCarouselIndex(1);
			element.style.transition = `all 0s`;
			element.style.transform = `translateY(-100%)`;
		} else {
			console.log("wait 5");

			setTimeout(() => {
				setCarouselIndex(CarouselIndex + 1);
				//element.style.transform = `translateY(100%)`;
				element.style.transform = `translateY(-${CarouselIndex * 100}%)`;
				element.style.transition = `all 0.5s`;
			}, 5000);
		}
		// setTimeout(() => {
		// 	console.log("tiking");
		// 	let element = document.getElementById("carousel_1_img")!;
		// 	element.classList.remove("active");
		// 	element.classList.add("active");
		// 	console.log(CarouselIndex + 1 + "    ====  " + CarouselArr.data.length);
		// 	setTimeout(() => {
		// 		if (CarouselIndex + 1 > CarouselArr.data.length) {
		// 			setCarouselIndex(0);
		// 			console.log("to 0");
		// 			setSwitchTracker({
		// 				src1: CarouselArr.data[CarouselIndex],
		// 				src2: CarouselArr.data[CarouselIndex + 1],
		// 			});
		// 			element.classList.remove("active");
		// 		} else if (CarouselIndex + 1 === CarouselArr.data.length) {
		// 			console.log("equal");
		// 			setCarouselIndex(0);
		// 			console.log(CarouselArr.data[CarouselArr.data.length - 1]);
		// 			console.log(CarouselArr.data[0]);
		// 			setSwitchTracker({
		// 				src1: CarouselArr.data[CarouselArr.data.length - 1],
		// 				src2: CarouselArr.data[0],
		// 			});
		// 			element.classList.remove("active");
		// 		} else {
		// 			setSwitchTracker({
		// 				src1: CarouselArr.data[CarouselIndex],
		// 				src2: CarouselArr.data[CarouselIndex + 1],
		// 			});
		// 			element.classList.remove("active");
		// 			let holder = CarouselIndex + 1;
		// 			setCarouselIndex(holder);
		// 			console.log(CarouselIndex + 1);
		// 		}
		// 	}, 500);
		// }, 5000);
	}
	return (
		<div id='hero_container'>
			<div id='hero_imgs'>
				<div id='hero_imgs_mapper'>
					{!Loading
						? CarouselArr.map((element, index) => {
								return (
									<img
										key={index}
										id={`carousel_${index}_img`}
										className='carousel_img'
										src={element}
									/>
								);
						  })
						: "loading"}
				</div>
			</div>
			<div id='hero_imgs_paging'></div>
		</div>
	);
}

export default Carousel;
